(1,	'Joint Declaration on the Doctrine of Justification',	3,	'Lutheran World Federation and the Catholic Church',	'1999',	10,	31),
(2,	'Sacrosantum Concilium',	4,	'Vatican Council II',	'1964',	NULL,	NULL),
