(1,	156,	1,	'[19] Taz. Denz., 1545'),
(1,	157,	1,	'[20] Taz. Mtaguso Mkuu Vatikano II, Konstitusio ya kidogma juu ya ufunuo wa kimungu Dei Verbum, 5'),
(1,	158,	1,	'[21] Taz. Mtaguso Mkuu Vatikano II, Konstitusio ya kidogma juu ya ufunuo wa kimungu Dei Verbum, 4'),
